import React, { useState, useEffect } from 'react'

function Home() {
    // let observer = new IntersectionObserver((e)=>{
    //     e.forEach((box)=>{
    //         if (box.isIntersecting){
    //             box.target.style.opacity = 1;
    //         }
    //         else{

    //         }
    //     });

    // });
    // let homepage = document.getElementsByClassName("homepage")[0];
    // observer.observe(homepage);

    // window.addEventListener("scroll", function(event){

    // 	var top = this.scrollY;

    // 	var layers = document.getElementsByClassName("parallax");
    // 	var layer, speed, yPos;
    // 	for (var i = 0; i < layers.length; i++) {
    // 		layer = layers[i];
    // 		speed = layer.getAttribute('data-speed');
    // 		var yPos = -(top * speed / 100);
    // 		layer.setAttribute('style', 'transform: translate3d(0px, ' + yPos + 'px, 0px)');

    // 	}
    // });

    const [scrollPosition, setScrollPosition] = useState(0);

    window.addEventListener('scroll', function () {
        setScrollPosition(window.scrollY);
    });

    return (
        <div className="homepage" style={{backgroundColor:'lightgreen', backgroundPositionY: -scrollPosition }}>
            <Introduce></Introduce>
            <Solution></Solution>
            <Description></Description>
            <SSH></SSH>
            <Linux></Linux>
            <Techstack></Techstack>

            <div className="home_background1" ></div>
            <div style={{ width: "100%", height: "5000px" }}></div>
        </div>
    );

}


function Introduce() {

    return (
        <div className="home_introduce">
            <Script title="KNOT" scripts="KNOT이란, KNOT is Not Only Ternimal 의 줄임말로 단순히 버튼으로 사용하는 ssh 가 아닌 터미널을 의미합니다."
                hname="직접 사용 해보기" styleType="intro_link"></Script>
            <Image source=""></Image>
        </div>
    );
}

function Solution() {
    return (
        <div className="home_solution">
            <Image source=""></Image>
            <Script title="Solution"
                scripts="우리의 목적은 더 쉽고 간단하게 사용할 수 있도록 UI기반의 터미널을 제공합니다."
                hname="자세히 알아보기..." styleType="default_link"></Script>
        </div>
    );
}

function Description() {
    return (
        <div className="home_description">
            <Script title="Description"
                scripts="KNOT Description script"
                hname="자세히 알아보기..." styleType="default_link"></Script>
            <Image source=""></Image>
        </div>
    );
}

function SSH(){
    return (
        <div className="home_SSH">
            <Script title={`What is "SSH"?`} 
            scripts={`SSH는 "Secure Shell"의 약자로, 네트워크 상에서 안전하게 원격 컴퓨터에 접속하고 통신할 수 있는 프로토콜입니다. SSH는 암호화 기술을 사용하여 데이터를 안전하게 전송하고, 원격 컴퓨터에 로그인하거나 원격으로 명령을 실행할 수 있는 기능을 제공합니다.

            SSH는 클라이언트와 서버 간의 암호화된 터널을 구축하여 데이터를 안전하게 전송합니다. 이를 통해 해커나 악의적인 사용자들이 네트워크 통신을 도청하거나 데이터를 변조하는 것을 방지할 수 있습니다. 또한, SSH는 사용자 인증을 통해 안전한 접속을 보장합니다. 비밀번호 또는 공개 키 기반의 인증 방식을 사용하여 접속할 수 있으며, 암호화된 세션을 통해 패스워드와 다른 중요한 정보를 안전하게 전송합니다.`} />
        </div>

    );
}

function Linux(){
    return (
        <div className="home_Linux">
            <Script title={`What is "Linux"?`} 
            scripts={`리눅스(Linux)는 개발자 Linus Torvalds가 개발한 오픈 소스 운영 체제(OS)입니다. 리눅스는 UNIX 계열의 운영 체제로, 다양한 하드웨어 아키텍처에서 동작할 수 있습니다. 리눅스는 안정성, 보안성, 확장성, 유연성 등 다양한 장점을 가지고 있어 서버, 개발 환경, 임베디드 시스템 등 다양한 분야에서 널리 사용됩니다.

            리눅스의 가장 중요한 특징 중 하나는 오픈 소스이며, GNU 일반 공중 사용 허가서(GPL)와 같은 자유 소프트웨어 라이선스를 따르고 있습니다. 이는 소스 코드에 대한 열람, 수정, 배포, 재배포 등 자유로운 이용을 허용합니다. 따라서 리눅스 커뮤니티에서는 다양한 개발자들이 참여하여 개선 및 확장을 진행하고 있습니다.`} />
        </div>

    );
}

function Techstack(props) {
    const src_dir = [
        "css3", "html5", "linux", "javascript", "react", "ssh",
    ]
    const windowSize = window.innerWidth
    const [positions, setPositions] = useState([
        0,
        windowSize * 0.155,
        windowSize * 0.31,
        windowSize * 0.465,
        windowSize * 0.62,
        windowSize * 0.775,
    ]);

    useEffect(() => {
        const intervalId = setInterval(() => {
            setPositions(prevPositions => {
                return prevPositions.map(prevPosition => {
                    const newPosition = prevPosition - 1;
                    if (newPosition <= windowSize * -0.13) {
                        return windowSize * 0.80;
                    }
                    return newPosition;
                });
            });
        }, 45);
        return () => clearInterval(intervalId);
    }, []);

    return (
        <>
            <h1>Tech Stacks</h1>
            <div style={{ height: "200px", position: 'relative', overflow: "hidden" }}>
                {positions.map((position, index) => (
                    <img key={index} src={require(`./src/image/tech_stack/${src_dir[index]}.png`)} style={{ width: "200px", height: "200px", position: 'absolute', left: position }} />
                ))}
            </div>
        </>

    );
}


function Script(props) {
    return (
        <div className="home_script">
            <h1>{props.title}</h1>
            <p>{props.scripts}</p>
            <a className={props.styleType} href={props.link} >{props.hname}</a>
        </div>
    );

}

function Image(props) {
    return (
        <div className="home_image">
            <img src={props.source}></img>
        </div>
    );
}

function Button(props) {
    return (
        <div className="home_button">
            <input type="button">{props.value}</input>
        </div>
    );
}

export default Home;
