import React, {useState, useEffect} from 'react'

function Home() {
    // let observer = new IntersectionObserver((e)=>{
    //     e.forEach((box)=>{
    //         if (box.isIntersecting){
    //             box.target.style.opacity = 1;
    //         }
    //         else{

    //         }
    //     });

    // });
    // let homepage = document.getElementsByClassName("homepage")[0];
    // observer.observe(homepage);

    // window.addEventListener("scroll", function(event){

    // 	var top = this.scrollY;

    // 	var layers = document.getElementsByClassName("parallax");
    // 	var layer, speed, yPos;
    // 	for (var i = 0; i < layers.length; i++) {
    // 		layer = layers[i];
    // 		speed = layer.getAttribute('data-speed');
    // 		var yPos = -(top * speed / 100);
    // 		layer.setAttribute('style', 'transform: translate3d(0px, ' + yPos + 'px, 0px)');

    // 	}
    // });

    return (
        <div className="homepage">
            <Introduce></Introduce>
            <Solution></Solution>
            <Description></Description>
            <Techstack></Techstack>
            <div className="home_background1"></div>
            <div style={{ width: "100%", height: "5000px" }}></div>
        </div>
    );

}


function Introduce() {

    return (
        <div className="home_introduce">
            <Script title="KNOT" scripts="KNOT Introduce script" hname="직접 사용 해보기" styleType="intro_link"></Script>
            <Image source=""></Image>
        </div>
    );
}

function Solution() {
    return (
        <div className="home_solution">
            <Image source=""></Image>
            <Script title="Solution" scripts="KNOT Solution script" hname="자세히 알아보기..." styleType="default_link"></Script>
        </div>
    );
}

function Description() {
    return (
        <div className="home_description">
            <Script title="Description" scripts="KNOT Description script" hname="자세히 알아보기..." styleType="default_link"></Script>
            <Image source=""></Image>
        </div>
    );
}

function Techstack(props) {
    const [position, setPosition] = useState(0);

    useEffect(() => {
        const intervalId = setInterval(() => {
            setPosition(prevPosition => {
                const newPosition = prevPosition + 10;
                if (newPosition > window.innerWidth * 0.8) {
                    return 0;
                }
                return newPosition;
            });
        }, 100);
        return () => clearInterval(intervalId);
    }, []);

    return (
        <div style={{ position: 'relative' }}>
          <img src={props.source} style={{ position: 'absolute', left: position }} />
        </div>
      );
}


function Script(props) {
    return (
        <div className="home_script">
            <h1>{props.title}</h1>
            <p>{props.scripts}</p>
            <a className={props.styleType} href={props.link} >{props.hname}</a>
        </div>
    );

}

function Image(props) {
    return (
        <div className="home_image">
            <img src={props.source}></img>
        </div>
    );
}

function Button(props) {
    return (
        <div className="home_button">
            <input type="button">{props.value}</input>
        </div>
    );
}

export default Home;
