function ChatBot() {
    // const [typing, setTyping] = useState(false);
    // const [messages, setMessages] = useState([
    //     {
    //         message: "Hello",
    //         sender: "ChatGPT"
    //     }
    // ]);

    // const handleSend = async (message) =>{
    //     const newMessage = {
    //         message: message,
    //         sender: "user", 
    //         direction : "outgoing"
    //     }

    //     const newMessages = [...message, ];

    //     setMessages(newMessages);

    // }
    const [isChatting, setIsChatting] = useState(false);
    const [visible, setVisible] = useState([
        {
            display: "none",
            position: "relative", 
            height: "800px",
            width: "700px"
        }
    ])
    const [messages, setMessages] = useState([
        {
            message: "Hello, I'm ChatGPT! Ask me anything!",
            sentTime: "just now",
            sender: "ChatGPT"
        }
    ]);
    const [isTyping, setIsTyping] = useState(false);

    const handleSend = async (message) => {
        const newMessage = {
            message,
            direction: 'outgoing',
            sender: "user"
        };

        const newMessages = [...messages, newMessage];

        setMessages(newMessages);

        // Initial system message to determine ChatGPT functionality
        // How it responds, how it talks, etc.
        setIsTyping(true);
        await processMessageToChatGPT(newMessages);
    };

    async function processMessageToChatGPT(chatMessages) { // messages is an array of messages
        // Format messages for chatGPT API
        // API is expecting objects in format of { role: "user" or "assistant", "content": "message here"}
        // So we need to reformat

        let apiMessages = chatMessages.map((messageObject) => {
            let role = "";
            if (messageObject.sender === "ChatGPT") {
                role = "assistant";
            } else {
                role = "user";
            }
            return { role: role, content: messageObject.message }
        });


        // Get the request body set up with the model we plan to use
        // and the messages which we formatted above. We add a system message in the front to'
        // determine how we want chatGPT to act. 
        const apiRequestBody = {
            "model": "gpt-3.5-turbo",
            "messages": [
                systemMessage,  // The system message DEFINES the logic of our chatGPT
                ...apiMessages // The messages from our chat with ChatGPT
            ]
        }

        await fetch("https://api.openai.com/v1/chat/completions",
            {
                method: "POST",
                headers: {
                    "Authorization": "Bearer " + API_KEY,
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(apiRequestBody)
            }).then((data) => {
                return data.json();
            }).then((data) => {
                console.log(data);
                setMessages([...chatMessages, {
                    message: data.choices[0].message.content,
                    sender: "ChatGPT"
                }]);
                setIsTyping(false);
            });
    }

    function chageToggle(){
        setIsChatting(isChatting ? false : true);
        if (isChatting){
            setVisible.display = "none";

        }else{

        }
    }
    return (
        <div className="home_chatbot">
            <div style={visible}>
                <MainContainer>
                    <ChatContainer>
                        <MessageList
                            scrollBehavior="smooth"
                            typingIndicator={isTyping ? <TypingIndicator content="ChatGPT is typing" /> : null}
                        >
                            {messages.map((message, i) => {
                                console.log(message)
                                return <Message key={i} model={message} />
                            })}
                        </MessageList>
                        <MessageInput placeholder="Type message here" onSend={handleSend} />
                    </ChatContainer>
                </MainContainer>
                <div className="home_chat_button">
                    <input type="button" onClick={chageToggle} value={"Learn more with GPT"}></input>
                </div>
            </div>
        </div>


    );
}

function Interaction() {
    const { toggle, setToggle } = useState(0);
    const data = [["Command", "Frequency", { role: "style" }],
    ["ls", 3435, "red"],
    ["cd", 3132, "red"],
    ["git", 2387, "blue"],
    ["python", 1879, "blue"],
    ["rm", 1682, "blue"],
