    ["mv", 311, "blue"],
    ["which", 265, "blue"],
    ["ssh", 111, "blue"],
    ["cp", 99, "blue"],
    ["mkdir", 24, "blue"]
    ];

    const option = {
        title: "frequently executed commands",
        hAxis: { title: "Command" },
        vAxis: { title: "Frequency" },
